import { FC, useMemo } from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import { Box, Dialog } from '@material-ui/core';
import { Close as Icon } from '@material-ui/icons';
import { useWallet } from 'contexts/wallet';
import { AVAILABLE_NETWORKSID, AVAILABLE_NETWORKS } from 'config';

const useStyles = makeStyles((theme) => ({
  container: {
    width: 450,
    padding: '0 20px 10px',
    lineHeight: '1.5rem',
    '& button': {
      width: '100%',
      padding: '10px 0',
      marginTop: 20,
      fontSize: 18,
    },
  },
  x: {
    position: 'absolute',
    top: 5,
    right: 5,
    cursor: 'pointer',
  },
  wallet: {
    display: 'flex',
    alignItems: 'center',
    cursor: 'pointer',
    margin: '10px 0',
    '& img': {
      marginRight: 15,
    },
    '&:hover': {
      opacity: 0.8,
    },
  },
  net: {
    color: theme.palette.secondary.main,
  },
}));

export const ConnectWallet: FC = () => {
  const classes = useStyles();
  const wallet = useWallet();

  const isOnCorrectNetwork = useMemo(
    () => !wallet.chainid || ~AVAILABLE_NETWORKSID.indexOf(wallet.chainid),
    [wallet.chainid]
  );

  return (
    <Dialog
      onClose={() => {}}
      aria-labelledby='wrong-network-prompt'
      open={!isOnCorrectNetwork || wallet.isConnecting}
    >
      <div className={clsx('flex', 'flex-grow', 'flex-col', classes.container)}>
        {isOnCorrectNetwork ? (
          <>
            <div className={classes.x}>
              <Icon style={{ fontSize: 20 }} onClick={wallet.stopConnecting} />
            </div>
            <h3>Connect Wallet</h3>
            <div className={clsx('flex', 'flex-col')}>
              <div
                onClick={wallet.connectMetamask}
                className={clsx(classes.wallet)}
              >
                <img
                  src='wallets/metamask.svg'
                  width='35'
                  height='35'
                  alt='metamask'
                />
                <div>Metamask</div>
              </div>
              {/*
                <div onClick={wallet.connectWalletConnect} className={clsx(classes.wallet)}>
                  <img
                    src='wallets/wallet-connect.png'
                    width='35'
                    height='35'
                    alt='wallet connect'
                  />
                  <div>Wallet Connect</div>
                </div>
              */}
            </div>
          </>
        ) : (
          <Box
            mt={2}
            className={clsx(
              'flex',
              'flex-col',
              'items-center',
              'justify-center',
              'text-center'
            )}
          >
            <strong>
              Please connect to {AVAILABLE_NETWORKS.join(' or ')}.
            </strong>
            <div>or</div>
            <div className='cursor-pointer' onClick={wallet.disconnect}>
              disconnect
            </div>
          </Box>
        )}
      </div>
    </Dialog>
  );
};

export default ConnectWallet;
