import { FC, useCallback, useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { withRouter } from 'react-router-dom';
import * as ethers from 'ethers';
import { Box, Paper } from '@material-ui/core';
import { positions } from '@material-ui/system';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableHead from '@material-ui/core/TableHead';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Tooltip from '@material-ui/core/Tooltip';
import InfoIcon from '@material-ui/icons/Info';
import moment from 'moment';
import dragon from './dragon.png';
import bell from './bell.gif';
import { useWallet } from 'contexts/wallet';
import { useContracts } from 'contexts/contracts';
import { useNotifications } from 'contexts/notifications';
import { useData } from 'contexts/data';
import { LiquidityPosition } from 'utils/types';
import { formatUnits } from 'utils/big-number';

export const useStyles = makeStyles((theme) => ({
  maxButton: {
    height: 35,
  },
  depositButtonCell: {
    width: 110,
    padding: 5,
  },
  depositButton: {
    width: 100,
  },
}));

const Mint: FC<{ history: any }> = ({ history }) => {
  const classes = useStyles();
  const { nftManagerPositionsContract } = useContracts();
  const { startConnecting: startConnectingWallet, address } = useWallet();
  const {
    token0Address,
    token1Address,
    token0Symbol,
    token1Symbol,
  } = useContracts();
  const {
    positions,
    currentIncentiveId,
    incentives,
    setCurrentIncentiveId,
  } = useData();

  const isholder = async () => {
    if (!nftManagerPositionsContract) return false;
    const [balance] = await Promise.all([
      nftManagerPositionsContract.balanceof(address),
    ]);
    if (balance > 0) return true;
    else return false;
  };
  const mintBell = async () => {
    if (!nftManagerPositionsContract) return;
    const [minting] = await Promise.all([
      nftManagerPositionsContract.getApproved(address),
    ]);
  };
  return (
    <>
      <div style={{ fontSize: '5rem', color: '#332B1C' }}>The Tale begins</div>
      <div style={{ fontSize: '1.3rem', marginLeft: '3%', marginTop: '5%' }}>
        The Bell of Dojoji is a tale that must be written on the Blockchain and
        live it's glory for the future generations!
        <p>
          A{' '}
          <a style={{ fontSize: '1.4rem', color: '#332B1C' }}>
            trustless contract
          </a>{' '}
          has been deployed where each free mint will ring the Bell...
        </p>
        <p>
          Upon reaching MaxSupply of 777 Bells the trading of{' '}
          <a style={{ fontSize: '1.4rem', color: '#332B1C' }}>$DOJOJI</a> begins
          and its full glory shall be unleashed!
        </p>
        <p>
          The Original Ringers NFT holders will have tax privileges on the token
          and a few surprizes for the longrun...{' '}
        </p>
      </div>
      <Box
        m={5}
        style={{
          display: 'flex',
          flexDirection: 'row',
          width: '100%',

          marginLeft: '0',
          marginTop: '10%',
        }}
      >
        <Paper>
          <Box display={'flex'} flexDirection={'row'}>
            <Box>
              <img
                src={bell}
                alt='cloud'
                style={{ position: 'relative', width: '100%',margin: '10%' }}
              />
            </Box>
            <Button
              color='secondary'
              variant='contained'
              onClick={mintBell}
              className={classes.depositButton}
              style={{
                fontSize: '1.1rem',
                width: '72%',
              }}
            >
              Mint {'&'} Ring
            </Button>
          </Box>
        </Paper>
        <Box>
          <div>
            With each Ring of the Bell we become stronger and closer to the
            release of{' '}
            <a style={{ fontSize: '1.4rem', color: '#332B1C' }}>$DOJOJI</a>
            <p>
              Don't be greedy in the mint the purpose is to create a strong and
              united community of Chads ready to make this fly!
            </p>
          </div>
        </Box>
      </Box>
    </>
  );
};

const LiquidityPositionTableRow: FC<{
  position: LiquidityPosition;
  history: any;
}> = ({ position, history }) => {
  const classes = useStyles();
  const { address } = useWallet();
  const { token0Decimals } = useContracts();

  const stake = useCallback(async () => {
    history.push(`/stake/${position.tokenId}`);
  }, [position.tokenId, history]);

  const unstake = useCallback(async () => {
    history.push(`/unstake/${position.tokenId}`);
  }, [position.tokenId, history]);

  const withdraw = useCallback(async () => {
    history.push(`/withdraw/${position.tokenId}`);
  }, [position.tokenId, history]);

  return (
    <TableRow>
      <TableCell component='th' scope='row'>
        {position.tokenId.toString()}
      </TableCell>
      <TableCell>
        {!position.reward.isZero() ? (
          <Box className='flex items-center'>
            <Box mr={1}>{formatUnits(position.reward, token0Decimals)}</Box>
            <Tooltip
              title='Unstake position in order to claim accrued rewards.'
              arrow
              placement='top'
            >
              <Box className='flex items-center cursor'>
                <InfoIcon fontSize='small' />
              </Box>
            </Tooltip>
          </Box>
        ) : (
          '-'
        )}
      </TableCell>
      <TableCell align='right' className={classes.depositButtonCell}>
        <Button
          color='secondary'
          variant='contained'
          onClick={position.staked ? unstake : stake}
          className={classes.depositButton}
        >
          {position.staked ? 'Unstake' : 'Stake'}
        </Button>
      </TableCell>
      <TableCell align='right' className={classes.depositButtonCell}>
        <Button
          color='secondary'
          variant='contained'
          onClick={withdraw}
          className={classes.depositButton}
          disabled={position.owner === address}
        >
          Withdraw
        </Button>
      </TableCell>
    </TableRow>
  );
};

const ClaimAvailableReward: FC = () => {
  const classes = useStyles();
  const { stakingRewardsContract } = useContracts();
  const {
    currentIncentiveRewardTokenSymbol,
    currentIncentiveRewardTokenDecimals,
    currentIncentive,
  } = useData();
  const { address } = useWallet();
  const { tx } = useNotifications();

  const [reward, setReward] = useState(ethers.BigNumber.from(0));
  const [isClaiming, setIsClaiming] = useState(false);

  useEffect(() => {
    if (!(stakingRewardsContract && currentIncentive && address)) return;

    let isMounted = true;
    const unsubs = [
      () => {
        isMounted = false;
      },
    ];

    const load = async () => {
      const reward = await stakingRewardsContract.rewards(
        currentIncentive.key.rewardToken,
        address
      );
      if (isMounted) setReward(reward);
    };

    const subscribe = () => {
      const tokenUnstakedEvent = stakingRewardsContract.filters.TokenUnstaked();
      stakingRewardsContract.on(tokenUnstakedEvent, load);
      unsubs.push(() => {
        stakingRewardsContract.off(tokenUnstakedEvent, load);
      });

      const rewardClaimedEvent = stakingRewardsContract.filters.RewardClaimed();
      stakingRewardsContract.on(rewardClaimedEvent, load);
      unsubs.push(() => {
        stakingRewardsContract.off(rewardClaimedEvent, load);
      });
    };

    load();
    subscribe();

    return () => {
      unsubs.map((u) => u());
    };
  }, [stakingRewardsContract, currentIncentive, address]);

  const claim = async () => {
    if (!(stakingRewardsContract && currentIncentive)) return;

    try {
      setIsClaiming(true);
      const reward = await stakingRewardsContract.rewards(
        currentIncentive.key.rewardToken,
        address
      );
      await tx('Claiming...', 'Claimed!', () =>
        stakingRewardsContract.claimReward(
          currentIncentive.key.rewardToken,
          address,
          reward
        )
      );
    } catch (e) {
      console.warn(e);
    } finally {
      setIsClaiming(false);
    }
  };

  return (
    <Box className='flex items-center'>
      <Box mr={1}>REWARDS:</Box>{' '}
      <Box mr={2}>
        {formatUnits(reward, currentIncentiveRewardTokenDecimals)}{' '}
        {currentIncentiveRewardTokenSymbol}
      </Box>
      <Button
        color='secondary'
        variant='contained'
        onClick={claim}
        className={classes.depositButton}
        disabled={isClaiming || reward.isZero()}
      >
        {isClaiming ? 'Claiming...' : 'Claim'}
      </Button>
    </Box>
  );
};

function formatTimestamp(unix: number) {
  return moment.unix(unix).local().format('YYYY-MM-DD HHmm[h]');
}

export default withRouter(Mint);
