"use strict";

var _utils = require("@0xproject/utils");

var HEX_REGEX = /^0x[0-9A-F]*$/i;
expect.extend({
  toBeHex: function toBeHex(received) {
    var pass = typeof received === 'string' && HEX_REGEX.test(received);

    if (pass) {
      return {
        message: function message() {
          return "expected ".concat(received, " to be a Hex String");
        },
        pass: true
      };
    } else {
      return {
        message: function message() {
          return "expected ".concat(received, " to be a Hex String");
        },
        pass: false
      };
    }
  },
  toBeEthAddress: function toBeEthAddress(received) {
    var pass = typeof received === 'string' && _utils.addressUtils.isAddress(received);

    if (pass) {
      return {
        message: function message() {
          return "expected ".concat(received, " to be an Eth address");
        },
        pass: true
      };
    } else {
      return {
        message: function message() {
          return "expected ".concat(received, " to be an Eth address");
        },
        pass: false
      };
    }
  }
});